mapper.delete:
DELETE 
FROM APPOINTMENTINFO
	WHERE mName = #{param1} 
	  AND mJumin1=#{param2} 
	  AND mJumin2=#{param3}

mapper.update:
UPDATE APPOINTMENTINFO
SET appCk=#{param4}
	WHERE mName = #{param1} 
		  AND mJumin1=#{param2} 
		  AND mJumin2=#{param3}

mapper.select:
	   	SELECT *  
	   	FROM APPOINTMENTINFO
	   		WHERE appName= #{param1}  
	   		  AND mJumin1 = #{param2}  
	   		  AND mJumin2 = #{param3} 
         ORDER BY appdate ASC, apptime ASC

	   	SELECT *  
	   	FROM APPOINTMENTINFO
	   		WHERE mName= #{param1}  
	   		  AND mJumin1 = #{param2}  
	   		  AND mJumin2 = #{param3} 
         ORDER BY appdate ASC, apptime ASC

	SELECT *  
	FROM APPOINTMENTINFO
		WHERE appName= #{param3}  
		  AND mJumin1 = #{param4}  
		  AND mJumin2 = #{param5} 

		TO_DATE(appdate,'YYYY-MM-DD')  >= TO_DATE(#{param1},'YYYY-MM-DD') AND TO_DATE(appdate,'YYYY-MM-DD') <= TO_DATE(#{param2},'YYYY-MM-DD')

     ORDER BY appdate ASC, apptime ASC

	SELECT *  
	FROM APPOINTMENTINFO
		WHERE mName= #{param3}  
		  AND mJumin1 = #{param4}  
		  AND mJumin2 = #{param5} 

		TO_DATE(appdate,'YYYY-MM-DD')  >= TO_DATE(#{param1},'YYYY-MM-DD') AND TO_DATE(appdate,'YYYY-MM-DD') <= TO_DATE(#{param2},'YYYY-MM-DD')

     ORDER BY appdate ASC, apptime ASC

	SELECT *  
	FROM APPOINTMENTINFO
		WHERE appName = #{param3} 
		  AND mName= #{param4}  
		  AND mJumin1 = #{param5}  
		  AND mJumin2 = #{param6} 

		TO_DATE(appdate,'YYYY-MM-DD')  >= TO_DATE(#{param1},'YYYY-MM-DD') AND TO_DATE(appdate,'YYYY-MM-DD') <= TO_DATE(#{param2},'YYYY-MM-DD')

     ORDER BY appdate ASC, apptime ASC
